00061  template <class T>
00062 void Domain<T>::allocatePointers()
00063 {
00064     //cout <<" process " << id << " sub_imax " << this->sub_imax << " sub_jmax " << sub_jmax << endl;
00065 
00066     // Allocate memory to the pointers and initialize to zero 
00067     old_grid_1d = new T[sub_imax * sub_jmax]{0};     // set to total elements
00068     current_grid_1d = new T[sub_imax * sub_jmax]{0};
00069     new_grid_1d = new T[sub_imax * sub_jmax]{0};
00070 
00071     old_grid_2d = new T * [sub_imax];           // set to total number of rows
00072     current_grid_2d = new T * [sub_imax];
00073     new_grid_2d = new T * [sub_imax];
00074 
00075 
00076 
00077     // point the 2D array to the 1D array for the domain
00078     for (int i = 0; i < sub_imax; i++)
00079     {
00080         old_grid_2d[i] = &old_grid_1d[i * sub_jmax];            // set to total number of rows
00081         current_grid_2d[i] = &current_grid_1d[i * sub_jmax];
00082         new_grid_2d[i] = &new_grid_1d[i * sub_jmax];
00083     }
00084     
00085 }
