00111 template <class T>
00112 void Domain<T>::createSubDomains(int p)
00113 {
00114     int min_gap = p;
00115     int top = sqrt(p) + 1;
00116     for (int i = 1; i <= top; i++)
00117     {
00118         if (p % i == 0)
00119         {
00120             int gap = abs(p / i - i);
00121             if (gap < min_gap)
00122             {
00123                 min_gap = gap;
00124                 this->sub_rows = i;
00125                 this->sub_cols = p / i;
00126             }
00127         }
00128         
00129     }
00130 
00131 
00132     /*if (id == 0)
00133         cout << "Divide " << p << " into " << this->sub_rows << " by " << this->sub_cols << " grid" << endl;*/
00134 
00135 }
