00137 template <class T>
00138 void Domain<T>::setSubDomainSize()
00139 {
00140     // default processor variables
00141     num_rows = new int[sub_rows]{};
00142     start_row = new int[sub_rows]{};
00143     num_columns = new int[sub_cols]{};
00144     start_column = new int[sub_cols]{};
00145 
00146     int start_r = 0;
00147     for (int k = 0; k < this->sub_rows; k++)
00148     {
00149         // Dividing the rows among the processes
00150         num_rows[k] = (this->global_jmax - start_r) / (this->sub_rows-k);
00151         start_row[k] = start_r; // set the first start point as 0
00152 
00153         // incrementing start point to the next start
00154         start_r += num_rows[k];
00155     }
00156     //cout << "process " << id <<  "  id / sub_cols" << id / sub_cols << endl;
00157     //cout << "process "<< id << " num_rows " << num_rows[id /sub_cols] << " start_row " << start_row[id / sub_cols] << endl;
00158 
00159     int start_c = 0;
00160     for (int k = 0; k < this->sub_cols; k++)
00161     {
00162         // Dividing the columns among the processes
00163         num_columns[k] =(this->global_imax - start_c) / (this->sub_cols-k);
00164         start_column[k] = start_c;
00165 
00166         start_c += num_columns[k];
00167     }
00168 
00169     //cout << "process " << id << " num_columns " << num_columns[id%sub_cols ] << " start_column " << start_column[id % sub_cols] << endl;
00170     //cout << "process" << id << " num_rows " << num_rows[id /sub_cols] << " start_row " << start_row[id / sub_cols] << endl;
00171     
00172     // With padding the maximum value of the subdomain is incremented by 2
00173     this->sub_imax = this->num_rows[id / sub_cols] + 2;
00174     this->sub_jmax = this->num_columns[id % sub_cols] + 2;
00175 
00176     // Set subdomain variables to processor indices
00177     this->sub_i = id / sub_cols;
00178     this->sub_j = id % sub_cols;
00179 
00180     /*cout << "Process id : " << id << "sub_i " << this->sub_i << " this->sub_j " << this->sub_j<< endl;*/
00181     
00182     // Set Global domain parameters 
00183     this->global_i = start_row[id / sub_cols];
00184     this->global_j = start_column[id % sub_cols];
00185 
00186     //cout << " process " << id << " global_i " << this->global_i << " global_j " << this->global_j << endl;
00187     //cout << " For process " << id << " num_rows " << this->num_rows[id % sub_cols] << "num_columns " << this->num_columns[id / sub_cols]<<" Local Start i, j " << this->sub_i <<" "<< this->sub_j << " Global Start i, j " << this->global_i << " " << this->global_j << endl;
