00195 {
00196 
00197     // set the neighbour array to -1
00198     for(int i = 0; i < 4; i++)
00199     {
00200         neighbours[i] = -1;
00201     }
00202 
00203     
00204         // Loop on left and right of the current processor position
00205         for (int i = -1; i <= 1; i++)
00206         {
00207             // Loop on top and bottom of the current processor position
00208             for (int j = -1; j <= 1; j++)
00209             {
00210                 //------------------------------------------------------------------------------|
00211                 // PERIODIC NEIGHBOURS
00212                 //------------------------------------------------------------------------------|
00213                 if (boundary == 2) // If the boubdary is periodic
00214                 {
00215                     // so we dont go negative we add sub_cols and sub_rows to the numbers
00216                     int neigh_i = (this->sub_j + i + this->sub_cols) % this->sub_cols;
00217                     int neigh_j = (this->sub_i + j + this->sub_rows) % this->sub_rows;
00218                     int neigh_id = neigh_i + neigh_j * this->sub_cols;
00219 
00220                     if (neigh_id != id)
00221                     {
00222                         if (i == -1 && j == 0) // left neighbour
00223                         {
00224                             this->neighbours[0] = neigh_id;
00225                         }
00226                         else if (i == 0 && j == -1) // top neighbour
00227                         {
00228                             this->neighbours[1] = neigh_id;
00229                         }
00230                         else if (i == 1 && j == 0) // right neighbour
00231                         {
00232                             this->neighbours[2] = neigh_id;
00233                         }
00234                         else if (i == 0 && j == 1) // bottom neighbour
00235                         {
00236                             this->neighbours[3] = neigh_id;
00237                         }
00238                     }
00239                 }
00240                 else
00241                 {
00242                     //------------------------------------------------------------------------------|
00243                     // NON PERIODIC NEIGHBOURS
00244                     //------------------------------------------------------------------------------|
00245                     // Find potential neighbours
00246                     int neigh_i = this->sub_j + i; // Find  left and right neighbours
00247                     int neigh_j = this->sub_i + j; // Find  top and bottom neighbours
00248 
00249                     // If found neighbours exist (ie they are non-negative), find the neightbour id 
00250                     if (neigh_i >= 0 && neigh_i < this->sub_cols && neigh_j >= 0 && neigh_j < this->sub_rows)
00251                     {
00252                         int neigh_id = neigh_i + neigh_j * this->sub_cols;
00253                         if (neigh_id != id)
00254                         {
00255                             if (i == -1 && j == 0) // left neighbour
