00288 template <class T>
00289 int Domain<T>:: id_from_index(int id_row, int id_column)
00290 {
00291     if (id_row >= sub_rows || id_row < 0)
00292         return -1;
00293     if (id_column >= sub_cols || id_column < 0)
00294         return -1;
00295 
00296     return id_row * sub_cols + id_column;
00297 }
